  </div>
</template>

<script lang="ts">
import Vue from 'vue'
import { makeExportString } from '@pagenote/shared/lib/utils/data'
import { contentToFile } from '@pagenote/shared/lib/utils/document'
import { WebPage } from '@pagenote/shared/lib/@types/data'
import LocalFileSystem from '@pagenote/shared/lib/library/localFileSystem'
import { decodeTextToWebPage } from '@/utils'

const exportPages = (pages: WebPage[]) => {
  const version = '0.20.14'
  const dataString = makeExportString({
    pages,
    resources: [],
    version: 2,
    extension_version: version,
    backup_at: Date.now(),
  })
  contentToFile(dataString, `${version}_${pages.length}.pagenote`)
}

const lfs = new LocalFileSystem({})

export default Vue.extend({
  name: 'DataResolver',
  data(): {
    input: string
    secretKey: string
    files: string[]
    logs: string[]
    deleteUseless: boolean
    pages: WebPage[]
  } {
    return {
      input:
        '',
      secretKey: '',
      files: [],
      logs: [],
      deleteUseless: false,
      pages: [],
    }
  },
  computed: {
    result(): string {
      return this.input
        ? decodeTextToWebPage(this.input, this.secretKey)
          ? ''
          : '解密失败了'
        : '请填入加密数据'
    },
  },
  methods: {
    setDir() {
      if (!this.secretKey) {
        alert('请输入加密密钥，用于解密云盘数据')
        return
      }
      lfs.setRoot().then((result) => {
        if (result) {
          lfs.readdir('/').then((result) => {
            this.files = result
            const pages: WebPage[] = []
            const tasks = []

            for (let i = 0; i < result.length; i++) {
              const file = result[i]
              tasks.push(
                new Promise((resolve) => {
                  lfs.readFile('/' + file).then((text) => {
                    if (text.length > 100) {
                      const page = decodeTextToWebPage(text, this.secretKey)
                      if (page) {
                        pages.push(page)
                        this.pages = pages
                        this.logs.push(`${file} : 解密成功`)
                      } else {
                        this.logs.push(`${file}: 解密失败或无效文件，请检查`)
                      }
                    } else {
                      this.logs.push(
                        `${file}: 这是一个空数据文件 ${
                          this.deleteUseless
                            ? '已经将其自动删除'
                            : '建议你将其删除'
                        }`
                      )
                      lfs.unlink('/' + file)
                    }
                    resolve(null)
                  })
                })
              )
            }

            Promise.all(tasks).then(function () {
              const checkConfirm = confirm(
                `成功解析到 ${pages.length} 个文件，确定导出吗？`
              )
              if (checkConfirm) {
                exportPages(pages)
              }
            })
          })
        }
      })
    },
    exportFile() {
      if (this.pages.length === 0) {
        alert('没有可导出的数据')
        return
      }
      exportPages(this.pages)
    },
  },
})
</script>
